ch');       if('1' == get_option('email_radio'))     {          $checked="checked";                           }     else if('2' == get_option('email_radio')){          $checked1="checked";                    }            if('on' == get_option('modern_check_captch'))      {          $moderncheck = "checked";           $required="required";                }      else{          $moderncheck = "";          $required=" ";      }                                        ?><form method="post" name="modern_form" action="" id="modern_form" class="formstyle">        <h1 class="texth">Contact form</h1>    <h3 class="texth">Add [modern_contact] short code in post</h3>        <table class="mtable">        <tr><td align="center" colsapn="2"><label>Wordpress User:</label></td>            <td><input type="text" disabled value="<?php echo $current_user->display_name; ?>"/></td></tr>               <tr><td><label>Admin Email:</label></td><td><input type="radio" name="selectingemail" value="1" <?php echo $checked; ?>/>        <input disabled="true" name="demail" type="text" value="<?php echo $uemail; ?> "/><small>Set an email address which will be used for sending messages.</small></td></tr>        <tr><td><label>Use This Email:</label></td><td><input type="radio" name="selectingemail" value="2" <?php echo $checked1; ?>/>        <input type="email" <?php echo $required; ?> value="<?php echo get_option('email_used');?>" name="useemail"/><small>Set an email address which will be used for sending message.</small></td></tr></table>    <div><input type="checkbox" name="addcaptcha" id="addcaptcha"<?php echo $moderncheck; ?>/><h3 class="texth">Add Captcha to your contact form:<h3><small class="small">(Must Enter Private & Public Key)</small></div>    <div class="two">        <table>        <tr><td><label>Private Key:</td><td><input type="text" value="<?php echo get_option('modern_privatekey'); ?>" size="40" 